$(document).ready(function(){var btnScrollTop=$('.btn-scroll-top');var scrollTopOffset=250;btnScrollTop.click(function(){$(window).scrollTop(0);});if($(this).scrollTop()>scrollTopOffset){btn_scroll_top_show();}
$(window).scroll(function(){if($(this).scrollTop()>scrollTopOffset){btn_scroll_top_show();}else{btn_scroll_top_hide();}});function btn_scroll_top_show(){btnScrollTop.addClass('btn-scroll-top-show');}
function btn_scroll_top_hide(){btnScrollTop.removeClass('btn-scroll-top-show');}});